function ConnectionManager(t,e){return this.host=t,this.port=e,this.makeRequest=function(t){jQuery.get("http://"+this.host+":"+this.port+"/"+t,function(t){return $.parseJSON(t)})},this.testConnection=function(){return this.makeRequest("api/registerClient").success||!1},this.getVersionedFiles=function(){return this.makeRequest("api/getFileList")},this.getNumberRevisions=function(t){return this.makeRequest("api/getNumberRevisions/"+t).numRevisions},this.getRevisionByIndex=function(t,e){return this.makeRequest("api/getRevisionByIndex/"+t+"/"+e).content},this.getLatestRevision=function(t){var e=this.getNumberRevisions(t)-1;return this.getRevisionByIndex(e,t).content},this}
