function ConnectionManager(t,e){return this.host=t,this.port=e,this.makeRequest=function(t,e){try{jQuery.get("http://"+this.host+":"+this.port+"/"+t,function(t){e($.parseJSON(t))})}catch(t){e(null)}},this.testConnection=function(t){this.makeRequest("api/registerClient",t)},this.getVersionedFiles=function(t){return this.makeRequest("api/getFileList",t)},this.getNumberRevisions=function(t){return this.makeRequest("api/getNumberRevisions/"+t).numRevisions},this.getRevisionByIndex=function(t,e,i){return this.makeRequest("api/getRevisionByIndex/"+t+"/"+e).content},this.getLatestRevision=function(t,e){var i=this.getNumberRevisions(t)-1;return this.getRevisionByIndex(i,t).content},this}